import { Injectable } from '@angular/core';
import {DialogTransferService} from "../../../dialog-transfer.service";
import {BehaviorSubject, filter, from, map, Observable, of, switchMap, tap} from "rxjs";
import {message, UserDialog, chat, UserDataService} from "../../../user-data.service";
import {Action, AngularFirestore, DocumentReference, DocumentSnapshot} from "@angular/fire/compat/firestore";
import {arrayUnion, updateDoc} from "@angular/fire/firestore";

export interface ChatData {
  name: string,
  photo: string,
  ref: DocumentReference<chat>
}

export interface messageDB {
  content: string,
  senderId: DocumentReference,
  time: string
}

@Injectable({
  providedIn: 'root'
})

export class ChatDataService {
  chatData$ = new BehaviorSubject<ChatData | null>(null);
  messagesData$ = new BehaviorSubject<message[] | null>(null);

  onSendMessage(messageEl: HTMLTextAreaElement): void {
    const message = messageEl.value.trim();
    if (!message) return;

    messageEl.value = '';
    this.messageHeightCalc(messageEl);

    this.sendMessageToDB(
      this.chatData$.getValue()?.ref as DocumentReference<chat>,
      this.createMessageForDB(messageEl, message)
    )
  }

  messageHeightCalc(textarea: HTMLTextAreaElement): void {
    textarea.style.height = 'auto'; // Ð¡Ð±Ñ€Ð¾Ñ Ð²Ñ‹ÑÐ¾Ñ‚Ñ‹ Ð¿ÐµÑ€ÐµÐ´ Ð²Ñ‹Ñ‡Ð¸ÑÐ»ÐµÐ½Ð¸ÐµÐ¼ Ð½Ð¾Ð²Ð¾Ð¹ Ð²Ñ‹ÑÐ¾Ñ‚Ñ‹
    textarea.style.height = textarea.scrollHeight - 22 + 'px'; // Ð£ÑÑ‚Ð°Ð½Ð¾Ð²ÐºÐ° Ð½Ð¾Ð²Ð¾Ð¹ Ð²Ñ‹ÑÐ¾Ñ‚Ñ‹ Ð½Ð° Ð¾ÑÐ½Ð¾Ð²Ðµ Ð²Ñ‹ÑÐ¾Ñ‚Ñ‹ ÑÐ¾Ð´ÐµÑ€Ð¶Ð¸Ð¼Ð¾Ð³Ð¾
  }

  createMessageForDB(messageEl: HTMLTextAreaElement | null, message: string, user: DocumentReference = this.userData.curUserDoc?.ref): messageDB {
    return {
      content: message,
      senderId: user,
      time: new Date().toISOString()
    }
  }

  getMessagesData(dialog: UserDialog) {
    from(dialog.ref.get()).subscribe(chat =>
      this.setNewUserAndMessages(dialog.otherUserName, dialog.otherUserPhoto, dialog.ref, chat.data()?.message as message[])
    )
    return this.store.doc<chat>(`chats/${dialog.ref.id}`).snapshotChanges()
  }

  setNewUserAndMessages(name: string, photo: string, ref: DocumentReference<chat>, messages: message[]): void {
    this.chatData$.next({name, photo, ref})
    this.messagesData$.next(messages)
  }

  randomBoolean(): boolean {
    return Math.random() < 0.4;
  }

  onTestChat(test: Action<DocumentSnapshot<chat>>){
    const random = this.randomBoolean()
    if(test.payload.data()?.message.slice(-1)[0].senderId.id === 'test--main' && random) {
      const currentChat = this.chatData$.getValue()?.ref as DocumentReference<chat>;
      const senderId = this.store.doc<chat>(`users/H89Non7WmWztp02NPxMO`).ref;
      const messages = [
        'HelloðŸ‘‹! This message was sent automatically for you to see the application in action.',
        "It is sends with a 40% probability. You're lucky if you see it. Have a nice dayðŸŽ‰"
      ];

      //ÐžÑ‚Ð¿Ñ€Ð°Ð²ÐºÐ° Ñ‚ÐµÑÑ‚Ð¾Ð²Ñ‹Ñ… ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ð¹
      setTimeout(() =>
        this.sendMessageToDB(currentChat, this.createMessageForDB(null, messages[0], senderId))
      ,500)
       setTimeout(() =>
        this.sendMessageToDB(currentChat, this.createMessageForDB(null, messages[1], senderId))
      ,800)
    }
  }

  sendMessageToDB(chat: DocumentReference<chat>, message: messageDB): void {
    updateDoc(
      chat,
      {message: arrayUnion(message)}
    )
  }

  constructor(
    private userData: UserDataService,
    private dialogTrans: DialogTransferService,
    private store: AngularFirestore,
  ) {
    // ÐŸÐ¾Ð´Ð¿Ð¸ÑÐºÐ° Ð½Ð° Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ðµ Ð²Ñ‹Ð±Ñ€Ð°Ð½Ð½Ð¾Ð³Ð¾ Ð´Ð¸Ð°Ð»Ð¾Ð³Ð°
    dialogTrans.dialog$.pipe(
      filter(Boolean),
      switchMap((dialog: UserDialog) => this.getMessagesData(dialog)),

      filter(data => (data.payload.data()?.message.length as number) > (this.messagesData$.getValue()?.length as number)),
      filter(dialog => dialog.type === 'modified'),

      tap(data => (data.payload.id === 'test-chat')? this.onTestChat(data) : null),
      map(data => data.payload.data()?.message),
    ).subscribe(messages =>
      this.messagesData$.next(messages as message[])
    )
  }
}
