import {
  AfterViewChecked,
  AfterViewInit,
  Component,
  ElementRef,
  HostListener,
  OnDestroy,
  OnInit,
  ViewChild
} from '@angular/core';
import {chat, message, User} from "../../../user-data.service";
import {Router} from "@angular/router";
import {BehaviorSubject, filter, tap} from 'rxjs';
import {ChatDataService} from "./chat-data.service";

@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.scss']
})

export class ChatComponent implements OnInit, AfterViewChecked {
  @ViewChild('message', {static: false}) private messageInput!: ElementRef;

  @HostListener('document:keydown.escape', ['$event'])
  private handleEscapeKey(event: KeyboardEvent): void {
    this.router.navigateByUrl('') //Close component on Escape
  };

  @HostListener('document:keydown.enter', ['$event'])
  private handleEnterKey(event: KeyboardEvent): void {
    this.chatData.onSendMessage(this.messageInput.nativeElement)//Send message on Enter
  }

  public user$ = new BehaviorSubject<User>({
    name: 'Loading',
    photo: ''
  });
  public messages: message[] = [];

  constructor(
      private router: Router,
      public chatData: ChatDataService
  ) {}

  ngOnInit() {
    //Subscriptions on changes in messages and dialogs
    this.chatData.chatData$.pipe(
      filter(Boolean),
      tap(messages => this.user$.next(messages))
    ).subscribe();

    this.chatData.messagesData$.pipe(
      filter(Boolean),
      tap(messages => this.messages = messages)
    ).subscribe();
  }

  ngAfterViewChecked() {
    this.messageInput.nativeElement.focus();
  }

  public onKeyDown(event: KeyboardEvent): void {
    if (event.key === 'Enter' && !event.shiftKey)
      event.preventDefault();
  }
}
<div class="container">
  <header class="header flex">
    <div class="user flex" *ngIf="user$ | async as user">
      <div class="avatar-box">
        <img class="avatar" [attr.src]="user.photo" alt="user's avatar">
      </div>
      <div class="username">{{user.name}}</div>
    </div>
    <button mat-icon-button [matMenuTriggerFor]="menu" color="accent">
      <mat-icon>more_vert</mat-icon>
    </button>
    <mat-menu #menu="matMenu">
      <button mat-menu-item>
        <mat-icon>delete</mat-icon>
        <span>Delete chat</span>
      </button>
    </mat-menu>
  </header>
  <div class="messenger-container flex">
    <main class="main">
      <app-message [messages]="messages">
      </app-message>
    </main>
    <div class="input-field">
      <div class="input-container">
        <textarea class="input" #message placeholder="Message" (input)="this.chatData.messageHeightCalc(message)" (keydown)="onKeyDown($event)"></textarea>
        <button mat-icon-button color="accent" class="btn-send" (click)="this.chatData.onSendMessage(message)">
          <mat-icon class="menu-icon">send</mat-icon>
        </button>
      </div>
    </div>
  </div>
</div>
